    // add a text field , same as above
    multiForm.push(fd2);


    // add a file field
    //----------------------------06090260189957917
	//Content-Disposition: form-data; name="imgfile"; filename="multifile.png"
	//Content-Type: image/png
	//
	//imgfile-value
	//
	//...binary from file...
    multiForm.push(fd3);


    request.multi(url,{fields:multiForm},function(err,result){
